import express from 'express';
import bodyParser from 'body-parser';
import { MongoClient } from 'mongodb';

const app = express();
app.use(bodyParser.json());

const cors = require('cors');
app.use(cors());

require('dotenv').config({ path: 'src/.env' });

const port = process.env.PORT;

app.listen(port, () => {
    console.log(`Listening on port ${port}`)
});

const CONNECTION_URL = 'mongodb://localhost:27017';
const DATABASE_NAME = "tweetdb";
var database, collection;

const Twit = require('twit');
var client = new Twit({
    consumer_key: process.env.TWITTER_CONSUMER_KEY,
    consumer_secret: process.env.TWITTER_CONSUMER_SECRET,
    access_token: process.env.TWITTER_ACCESS_TOKEN_KEY,
    access_token_secret: process.env.TWITTER_ACCESS_TOKEN_SECRET
});

var stream = client.stream('statuses/filter', { track: 'facial recognition' })
stream.on('tweet', function(tweet) {

    console.log(tweet.user.name);
    console.log(tweet.user.screen_name);
    console.log(tweet.user.location);
    console.log(tweet.created_at);
    console.log(tweet.user.profile_image_url);
    console.log(tweet.text);
    console.log("----------------------------")

    MongoClient.connect(CONNECTION_URL, { useNewUrlParser: true }, (error, client) => {
        if (error) {
            throw error;
        }
        database = client.db(DATABASE_NAME);
        collection = database.collection("tweets");
        console.log("Connected to database: " + DATABASE_NAME);

        var tweetObject = {
            id: tweet.id,
            name: tweet.user.name,
            screenName: tweet.user.screen_name,
            location: tweet.user.location,
            profileImg: tweet.user.profile_image_url,
            text: tweet.text,
            createdAt: tweet.created_at,
            reply: tweet.reply_count,
            retweet: tweet.retweet_count
        };

        collection.insertOne(tweetObject, function(err, res) {
            if (err) throw err;
            console.log("Record inserted");
        });
    });
})

app.get("/articles", (req, res) => {
    MongoClient.connect(CONNECTION_URL, { useNewUrlParser: true }, (error, client) => {
        if (error) {
            throw error;
        }

        database = client.db(DATABASE_NAME);
        collection = database.collection("tweets");
        console.log("Connected to database: " + DATABASE_NAME);

        collection.find({}).toArray((error, result) => {
            if (error) {
                return res.status(500).send(error);
            }
            res.send(result);
        });
    });
});

