import React, {useState, useEffect} from 'react';
import Tweet from '../components/Tweet';


const TweetFeedPage = () => {
    
    const [tweetInfo, setTweetInfo] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(`http://localhost:8000/tweets`);
            const body = await result.json();
            setTweetInfo(body);
        }
        fetchData();
    }, []);
    
    function refreshPage() {
        window.location.reload();
    }

    function visitTwitter() {
        window.location.assign('https://twitter.com/login');
    }

    return (
        <>
        <Tweet tweets={tweetInfo}/>
        <div>
            <button onClick={refreshPage}>refresh tweets</button>
        </div>
        <div>
            <button onClick={visitTwitter}>join the conversation</button>
        </div>
        </>
    );
}

export default TweetFeedPage;
