import React, {Component} from 'react';
import TweetsSingle from './TweetsSingle';

/*
const needle = require('needle');
require('dotenv').config({ path: 'src/.env' });
const token = process.env.BEARER_TOKEN;

const endpointURL = 'https://api.twitter.com/2/tweets/search/recent'

async function getRequest() {

    const params = {
        'query': 'facial recognition'
    }

    const res = await needle('get', endpointURL, params, {
        headers: {
            "authorization": `Bearer ${token}`
        }
    })

    if (res.body) {
        return(
            <h1>res.body</h1>
        )
    } else {
        throw new Error('Unsuccessful request')
    }
}

(async() => {

    try {
        // Make request
        const response = await getRequest();
        return(
            <h1>{response}</h1>
        )
        //console.log(response)

    } catch (e) {
        console.log(e);
        process.exit(-1);
    }
    process.exit();
})();
*/

class Tweets extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tweets: [],
        };
    }

    componentDidMount() {
        const url = 'https://api.twitter.com/2/tweets/search/recent?query=facialrecognition';
        fetch(url, {mode: 'no-cors'})
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                this.setState({
                    tweets: data.data
                })
            })
            .catch((error) => console.log(error));
    }

    renderItems() {
        return this.state.tweets.map((item) => (
            <TweetsSingle key={item.id} item={item} />
        ));
    }

    render() {
        return(
            <ul>
                {this.renderItems()}
            </ul>
        );
    }
}

export default Tweets;
